$(function() {
  /**
   * Popover Menu
   * 
   * @require twitter-bootstrap/bootstrap.min.js
   */
  var $oPopoverMenu        = $('.popover-menu');
  var $oPopoverMenuContent = $('.popover-menu-content');
  
  $oPopoverMenu.popover({
    placement : 'bottom',
    trigger   : 'hover',
    title     : 'Dados',
    html      : true,
    content   : $oPopoverMenuContent.html()
  });
  
  /** 
   * Modal 
   * 
   * @require twitter-bootstrap/bootstrap.min.js
   */
  var $oModalElements = $('[data-toggle=modal]');
  var $oMyModal       = $('#myModal');
  
  $oModalElements.live('click', function(e) {
    var sUrl    = $(this).attr('modal-url');
    var sWidth  = $(this).attr('modal-width')  ? $(this).attr('modal-width')  : 'auto';
    var sHeight = $(this).attr('modal-height') ? $(this).attr('modal-height') : 'auto';

    $oMyModal.html('<h3>Processando...</h3>');
    $oMyModal.css({ 
      'height' : sHeight,
      'width'  : sWidth
    });
    
    $oMyModal.load(sUrl);
    
    e.preventDefault();
    return;
  });
  
  /**
   * Adiciona mascaras nos campos do formulario, conforme classe do campo
   * 
   * @require dbseller/db.jquery.extends.js
   */
  $().addMascarasCampos();
  $().adicionarNumeroCaracteresTextArea();
});

